import styles from '../styles/BackgroundGalaxy.module.scss'
import ReactDOM from 'react-dom'
import { useRef } from 'react';
import { useEffect } from 'react';

type Circle = {
  pos: Point;
  radius: number;
  color: string;
  active?: number;
  draw: () => void;
};

type Point = {
  x: number;
  y: number;
  originX: number;
  originY: number;
  circle?: Circle;
  closest?: Point[];
  active?: number;
};

function BackgroundGalaxy(){
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const pointsRef = useRef<Point[]>([]);
  const animateHeaderRef = useRef(true);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      if (ctx) {
        const width = window.innerWidth;
        const height = window.innerHeight;
        const target = {x:width/2,y:height/2};
        animateHeaderRef:Boolean: true;

        canvas.width = width;
        canvas.height = height;

        const initHeader = ()=>{
          //create points
          let points: Point[] = [];
          for (var x = 0;x < width;x = x + width/20) {
            for(var y = 0; y < height; y = y + height/20) {
              var px = x + Math.random()*width/20;
              var py = y + Math.random()*height/20;
              var p = {x: px, originX: px, y: py, originY: py };
              points.push(p);
            }
          }
          //find 5 neibor for each point
          const getDistance = (p1: Point, p2: Point) => {
            return Math.pow(p1.x - p2.x, 2) + Math.pow(p1.y - p2.y, 2);
          }
          for(var i = 0; i < points.length; i++) {
            var closest = [];
            var p1 = points[i];
            for(var j = 0; j < points.length; j++) {
              var p2 = points[j]
              if(!(p1 === p2)) {
                var placed = false;
                for(var k = 0; k < 5; k++) {
                  if(!placed) {
                    if(closest[k] === undefined) {
                      closest[k] = p2;
                      placed = true;
                    }
                  }
                }
                for(var l = 0; l < 5; l++) {
                  if(!placed) {
                    if(getDistance(p1, p2) < getDistance(p1, closest[l])) {
                      closest[l] = p2;
                      placed = true;
                    }
                  }
                }
              }
            }
            p1.closest = closest;
          }
        }
        const mouseMove = (e:MouseEvent) => {
          var posX =0,posY = 0;
          if (e.pageX || e.pageY) {
              posX = e.pageX;
              posY = e.pageY;
          }
          else if (e.clientX || e.clientY)    {
              posX = e.clientX + document.body.scrollLeft + document.documentElement.scrollLeft;
              posY = e.clientY + document.body.scrollTop + document.documentElement.scrollTop;
          }
          target.x = posX;
          target.y = posY;
      }
  
      const scrollCheck = () => {
          if(document.body.scrollTop > height) animateHeader = false;
          else animateHeader = true;
      }
  
      function resize() {
          width = window.innerWidth;
          height = window.innerHeight;
          largeHeader.style.height = height+'px';
          canvas.width = width;
          canvas.height = height;
      }
        const initAnimation = ()=>{

        }
        initHeader();
        initAnimation();
      }
    }
  },[])

  return ReactDOM.createPortal(
    <div>
      <canvas ref={canvasRef}></canvas>
    </div>,
    document.body
  )
}

export default BackgroundGalaxy
