import { createStore } from "redux";

interface State {
  count: number,
}

const initialState:State = {
  count: 0,
}

function reducer(state = initialState, action: any) {
  switch (action.type) {
    case "ADD-NUM":
      return {...state, count: state.count + action.addNum};
    default:
      return state;
  }
}

const store = createStore(reducer);

export { store };
export type { State };
